import './App.css';
import {useReducer, useState} from 'react'
import Square from './components/Square';
function App() {
  const [board,setBoard]=useState(["","","","","","","","",""]);
  const [player, setPlayer]=useState("X");

  const chooseSquare=(square)=>{
    setBoard(board.map((val, idx) =>{
      if(idx==square ){
        return player
      }

      return val;
    }))
  }
  return (
    <div className="App">
      <div className="board">
        <div className="row">
        <Square 
          val={board[0]}
          chooseSquare={()=>chooseSquare(0)}
        />
        <Square 
          val={board[1]}
          chooseSquare={()=>chooseSquare(1)}
        />
        <Square 
          val={board[2]}
          chooseSquare={()=>chooseSquare(2)}
        />
        </div>
        <div className="row">
        <Square />
        <Square />
        <Square />
        </div>
        <div className="row">
        <Square />
        <Square />
        <Square />
        </div>
      </div>
    </div>
  );
}

export default App;
